import { create } from "zustand";
import { persist, createJSONStorage } from "zustand/middleware";
import { produce } from "immer";

type SidebarSettings = { disabled: boolean; isHoverOpen: boolean };
type SidebarStore = {
  isOpen: boolean;
  isHover: boolean;
  settings: SidebarSettings;
  toggleOpen: () => void;
  setIsOpen: (isOpen: boolean) => void;
  setIsHover: (isHover: boolean) => void;
  getOpenState: () => boolean;
  setSettings: (settings: Partial<SidebarSettings>) => void;
};

export const useSidebar = create(
  persist<SidebarStore>(
    (set, get) => ({
      isOpen: true,
      isHover: false,
      settings: { disabled: false, isHoverOpen: false },
      toggleOpen: () => {
        set({ isOpen: !get().isOpen });
      },
      setIsOpen: (isOpen: boolean) => {
        set({ isOpen });
      },
      setIsHover: (isHover: boolean) => {
        set({ isHover });
      },
      getOpenState: () => {
        const state = get();
        return state.isOpen || (state.settings.isHoverOpen && state.isHover);
      },
      setSettings: (settings: Partial<SidebarSettings>) => {
        set(
          produce((state: SidebarStore) => {
            state.settings = { ...state.settings, ...settings };
          })
        );
      }
    }),
    {
      name: "sidebar",
      storage: createJSONStorage(() => localStorage)
    }
  )
);

import { useState, useEffect } from "react";
/**
 * This hook fix hydration when use persist to save hook data to localStorage
 */
export const useStore = <T, F>(
    store: (callback: (state: T) => unknown) => unknown,
    callback: (state: T) => F
) => {
    const result = store(callback) as F;
    const [data, setData] = useState<F>();

    useEffect(() => {
        setData(result);
    }, [result]);

    return data;
};

import { VehicleData } from "@/features/members/types";
import { useEffect, useState, useRef } from "react";

export const useVehicleWebSocket = () => {
    const [socket, setSocket] = useState<WebSocket | null>(null);
    const [vehicleData, setVehicleData] = useState<VehicleData[]>([]);
    const [isConnected, setIsConnected] = useState(false);
    const [error, setError] = useState<Event | null>(null);
    const latestData = useRef<Map<number, VehicleData>>(new Map());

    useEffect(() => {
        // Initialize WebSocket connection
        const ws = new WebSocket(process.env.NEXT_PUBLIC_SOCKET_SERVER_URI!);

        ws.onopen = () => {
            console.log("WebSocket Connected");
            setIsConnected(true);
        };

        ws.onmessage = (event) => {
            try {
                const data = JSON.parse(event.data);
                const items = Array.isArray(data) ? data : [data];

                setVehicleData(prev => {
                    let hasUpdates = false;
                    const updatedData = [...prev];

                    items.forEach(item => {
                        const existingIndex = prev.findIndex(v => v.id === item.id);

                        if (existingIndex >= 0) {
                            // Update existing item if different
                            if (JSON.stringify(prev[existingIndex]) !== JSON.stringify(item)) {
                                updatedData[existingIndex] = item;
                                latestData.current.set(item.id, item);
                                hasUpdates = true;
                            }
                        } else {
                            // Add new item
                            updatedData.push(item);
                            latestData.current.set(item.id, item);
                            hasUpdates = true;
                        }
                    });

                    return hasUpdates ? updatedData : prev;
                });
            } catch (error) {
                console.error("Error parsing WebSocket data:", error);
            }
        };

        ws.onclose = () => {
            console.log("WebSocket Disconnected");
            setIsConnected(false);
        };

        ws.onerror = (error) => {
            console.error("WebSocket Error:", error);
            setError(error);
        };

        setSocket(ws);

        // Cleanup function
        return () => {
            if (ws.readyState === WebSocket.OPEN) {
                ws.close();
            }
        };
    }, []);

    return {
        socket,
        vehicleData,
        isConnected,
        error,
    }
}

